from wine.order
where id = #{id,jdbcType=INTEGER}

mapper.update:
update wine.order

  
    status = #{param1.status,jdbcType=INTEGER},
  
  
    pay_status = #{param1.payStatus,jdbcType=INTEGER},
  
  
    pay_type = #{param1.payType,jdbcType=INTEGER},
  
  
    pay_time = #{param1.payTime,jdbcType=TIMESTAMP},
  
  
    update_time = #{param1.updateTime,jdbcType=TIMESTAMP},
  

where id = #{param1.id,jdbcType=INTEGER} 
and status = #{param2,jdbcType=SMALLINT} 
and pay_status = #{param3,jdbcType=SMALLINT}

mapper.select:
select count(id)
from wine.order 

    
        create_time  >=  #{beginTime,jdbcType=TIMESTAMP}
     
    
        and create_time  <=  #{endTime,jdbcType=TIMESTAMP}

select 
from wine.order 
 
    
        create_time  >=  #{beginTime,jdbcType=TIMESTAMP}
     
    
        and create_time  <=  #{endTime,jdbcType=TIMESTAMP} 
    

order by status asc, create_time desc 

    limit 
     #{offset,jdbcType=INTEGER},  
    #{pagesize,jdbcType=INTEGER}
